import React, { useState } from 'react';
import AllergenIcons from './AllergenIcons';
import DietaryIcons from './DietaryIcons';
import NutritionModal from './NutritionModal';

const MenuItemCard = ({ item, language, formatCategory }) => {
  const [showNutritionModal, setShowNutritionModal] = useState(false);

  return (
    <>
      <div className="menu-item-card perspective-1000">
        <div className="group relative bg-gradient-to-br from-white via-gray-50 to-white rounded-2xl overflow-hidden transition-all duration-700 hover:shadow-luxury border border-gold/10 hover:border-gold/30">
          {/* Premium metallic effect */}
          <div className="absolute inset-0 bg-gradient-to-tr from-gold/[0.03] via-transparent to-primary/[0.03] opacity-0 group-hover:opacity-100 transition-opacity duration-700"></div>

          {/* Animated border glow */}
          <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-700">
            <div className="absolute inset-[-1px] bg-gradient-to-r from-gold via-primary to-gold rounded-2xl blur-sm animate-pulse"></div>
          </div>

          {/* Main content with relative positioning */}
          <div className="relative bg-white rounded-2xl overflow-hidden">
            {/* Image section with premium overlay */}
            {item.image ? (
              <div className="relative h-64 overflow-hidden bg-gradient-to-br from-gray-900 to-gray-800">
                <img 
                  src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${item.image}`}
                  alt={language === 'ar' && item.nameAr ? item.nameAr : item.name}
                  className="w-full h-full object-cover opacity-90 transition-all duration-700 group-hover:scale-110 group-hover:opacity-100"
                />

                {/* Luxury gradient overlays */}
                <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent"></div>
                <div className="absolute inset-0 bg-gradient-to-br from-primary/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-700"></div>

                {/* Floating elements */}
                <div className="absolute top-0 left-0 right-0 p-6 flex justify-between items-start">
                  <div className="transform transition-all duration-500 group-hover:translate-y-1">
                    <span className="inline-block bg-black/40 backdrop-blur-xl text-white/90 px-4 py-2 rounded-full text-xs font-light tracking-widest uppercase border border-white/20">
                      {formatCategory(item.category)}
                    </span>
                  </div>

                  {/* Premium price display */}
                  <div className="transform transition-all duration-500 group-hover:translate-y-1">
                    <div className="bg-gradient-to-br from-gold via-yellow-400 to-gold p-[1px] rounded-2xl shadow-gold">
                      <div className="bg-black/80 backdrop-blur-xl rounded-2xl px-5 py-3">
                        <div className="text-3xl font-light text-transparent bg-clip-text bg-gradient-to-br from-gold via-yellow-300 to-gold">
                          {item.price}
                        </div>
                        {item.priceWithoutVat && (
                          <p className="text-[10px] text-gold/70 text-center mt-1 font-light tracking-wider">
                            {language === 'ar' ? 'بدون ضريبة' : 'EXCL. VAT'}
                          </p>
                        )}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Bottom gradient fade */}
                <div className="absolute bottom-0 left-0 right-0 h-32 bg-gradient-to-t from-white via-white/80 to-transparent"></div>
              </div>
            ) : (
              <div className="relative h-48 bg-gradient-to-br from-primary/5 via-gold/5 to-primary/5">
                <div className="absolute inset-0 bg-pattern opacity-10"></div>
                <div className="relative h-full flex flex-col items-center justify-center p-6">
                  <span className="inline-block bg-white/80 backdrop-blur text-primary px-6 py-2 rounded-full text-xs font-light tracking-widest uppercase shadow-lg mb-4 border border-primary/10">
                    {formatCategory(item.category)}
                  </span>
                  <div className="text-4xl font-light text-transparent bg-clip-text bg-gradient-to-br from-primary via-primary-dark to-primary">
                    {item.price}
                  </div>
                </div>
              </div>
            )}

            {/* Content section with luxury spacing */}
            <div className="relative px-6 pb-6 pt-2">
              {/* Title with elegant typography */}
              <h3 className="text-2xl font-playfair font-medium text-gray-800 mb-3 tracking-wide group-hover:text-primary transition-colors duration-500">
                {language === 'ar' && item.nameAr ? item.nameAr : item.name}
              </h3>

              {/* Description with refined style */}
              <p className="text-gray-600 text-sm leading-relaxed mb-6 line-clamp-2 font-light">
                {language === 'ar' && item.descriptionAr ? item.descriptionAr : item.description}
              </p>

              {/* Premium nutrition display */}
              <div className="flex items-center gap-3 mb-6">
                {item.calories && (
                  <div className="group/cal relative">
                    <div className="absolute inset-0 bg-primary/20 rounded-full blur-md opacity-0 group-hover/cal:opacity-100 transition-opacity duration-500"></div>
                    <div className="relative flex items-center gap-2 bg-gradient-to-br from-primary/10 to-primary/5 px-4 py-2 rounded-full border border-primary/20">
                      <svg className="w-4 h-4 text-primary" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M7,9.5C7,8.67 7.67,8 8.5,8C9.33,8 10,8.67 10,9.5C10,10.33 9.33,11 8.5,11C7.67,11 7,10.33 7,9.5M12,17.23C10.25,17.23 8.71,16.5 7.81,15.42L9.23,14C9.68,14.72 10.75,15.23 12,15.23C13.25,15.23 14.32,14.72 14.77,14L16.19,15.42C15.29,16.5 13.75,17.23 12,17.23M15.5,11C14.67,11 14,10.33 14,9.5C14,8.67 14.67,8 15.5,8C16.33,8 17,8.67 17,9.5C17,10.33 16.33,11 15.5,11Z"/>
                      </svg>
                      <span className="text-sm font-medium text-primary">{item.calories}</span>
                      <span className="text-xs text-primary/70 font-light">cal</span>
                    </div>
                  </div>
                )}

                {(item.walkMinutes || item.runMinutes) && (
                  <div className="flex items-center gap-2">
                    {item.walkMinutes && (
                      <div className="group/walk relative">
                        <div className="absolute inset-0 bg-gold/20 rounded-full blur-md opacity-0 group-hover/walk:opacity-100 transition-opacity duration-500"></div>
                        <div className="relative flex items-center gap-2 bg-gradient-to-br from-gold/10 to-gold/5 px-4 py-2 rounded-full border border-gold/20">
                          <svg className="w-4 h-4 text-gold" viewBox="0 0 24 24" fill="currentColor">
                            <path d="M14.12,10H19V8.2H15.38L13.38,4.87C13.08,4.37 12.54,4.03 11.92,4.03C11.74,4.03 11.58,4.06 11.42,4.11L6,5.8V11H7.8V7.33L9.91,6.67L6,22H7.8L10.67,13.89L13,17V22H14.8V15.59L12.31,11.05L13.04,8.18M14,3.8C15,3.8 15.8,3 15.8,2C15.8,1 15,0.2 14,0.2C13,0.2 12.2,1 12.2,2C12.2,3 13,3.8 14,3.8Z"/>
                          </svg>
                          <span className="text-sm font-medium text-gold">{item.walkMinutes}</span>
                          <span className="text-xs text-gold/70 font-light">min</span>
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>

              {/* Luxury divider */}
              <div className="relative mb-4">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-200"></div>
                </div>
                <div className="relative flex justify-center">
                  <span className="bg-white px-4">
                    <div className="w-1 h-1 bg-gold rounded-full"></div>
                  </span>
                </div>
              </div>

              {/* Premium dietary badges section */}
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  {/* Dietary icons with luxury style */}
                  {item.halal && (
                    <div className="group/diet relative">
                      <div className="absolute inset-0 bg-emerald-400/30 rounded-lg blur-md opacity-0 group-hover/diet:opacity-100 transition-opacity duration-500"></div>
                      <div className="relative w-9 h-9 bg-gradient-to-br from-emerald-50 to-emerald-100 rounded-lg flex items-center justify-center border border-emerald-200/50 shadow-sm" 
                           title={language === 'ar' ? 'حلال' : 'Halal'}>
                        <DietaryIcons type="halal" size="w-5 h-5" className="text-emerald-600" />
                      </div>
                    </div>
                  )}
                  {item.glutenFree && (
                    <div className="group/diet relative">
                      <div className="absolute inset-0 bg-green-400/30 rounded-lg blur-md opacity-0 group-hover/diet:opacity-100 transition-opacity duration-500"></div>
                      <div className="relative w-9 h-9 bg-gradient-to-br from-green-50 to-green-100 rounded-lg flex items-center justify-center border border-green-200/50 shadow-sm" 
                           title={language === 'ar' ? 'خالي من الجلوتين' : 'Gluten Free'}>
                        <span className="text-xs font-bold text-green-600">GF</span>
                      </div>
                    </div>
                  )}
                  {item.dairyFree && (
                    <div className="group/diet relative">
                      <div className="absolute inset-0 bg-sky-400/30 rounded-lg blur-md opacity-0 group-hover/diet:opacity-100 transition-opacity duration-500"></div>
                      <div className="relative w-9 h-9 bg-gradient-to-br from-sky-50 to-sky-100 rounded-lg flex items-center justify-center border border-sky-200/50 shadow-sm" 
                           title={language === 'ar' ? 'خالي من الألبان' : 'Dairy Free'}>
                        <span className="text-xs font-bold text-sky-600">DF</span>
                      </div>
                    </div>
                  )}
                  {item.nutFree && (
                    <div className="group/diet relative">
                      <div className="absolute inset-0 bg-purple-400/30 rounded-lg blur-md opacity-0 group-hover/diet:opacity-100 transition-opacity duration-500"></div>
                      <div className="relative w-9 h-9 bg-gradient-to-br from-purple-50 to-purple-100 rounded-lg flex items-center justify-center border border-purple-200/50 shadow-sm" 
                           title={language === 'ar' ? 'خالي من المكسرات' : 'Nut Free'}>
                        <span className="text-xs font-bold text-purple-600">NF</span>
                      </div>
                    </div>
                  )}
                  {item.vegetarian && (
                    <div className="group/diet relative">
                      <div className="absolute inset-0 bg-green-400/30 rounded-lg blur-md opacity-0 group-hover/diet:opacity-100 transition-opacity duration-500"></div>
                      <div className="relative w-9 h-9 bg-gradient-to-br from-green-50 to-green-100 rounded-lg flex items-center justify-center border border-green-200/50 shadow-sm" 
                           title={language === 'ar' ? 'نباتي' : 'Vegetarian'}>
                        <DietaryIcons type="vegetarian" size="w-5 h-5" className="text-green-600" />
                      </div>
                    </div>
                  )}
                  {item.vegan && (
                    <div className="group/diet relative">
                      <div className="absolute inset-0 bg-lime-400/30 rounded-lg blur-md opacity-0 group-hover/diet:opacity-100 transition-opacity duration-500"></div>
                      <div className="relative w-9 h-9 bg-gradient-to-br from-lime-50 to-lime-100 rounded-lg flex items-center justify-center border border-lime-200/50 shadow-sm" 
                           title={language === 'ar' ? 'نباتي صرف' : 'Vegan'}>
                        <span className="text-xs font-bold text-lime-600">VG</span>
                      </div>
                    </div>
                  )}

                  {/* Allergen indicators */}
                  {item.allergens && item.allergens.length > 0 && (
                    <>
                      <div className="w-px h-8 bg-gradient-to-b from-transparent via-gray-300 to-transparent mx-1"></div>
                      <div className="flex items-center gap-1">
                        {item.allergens.slice(0, 3).map((allergen, index) => (
                          <div key={allergen} className="w-8 h-8 rounded-full bg-red-50 border border-red-200/50 flex items-center justify-center">
                            <span className="text-xs font-medium text-red-600">!</span>
                          </div>
                        ))}
                        {item.allergens.length > 3 && (
                          <span className="text-xs text-gray-500 ml-1">+{item.allergens.length - 3}</span>
                        )}
                      </div>
                    </>
                  )}
                </div>

                {/* Premium info button */}
                <button
                  onClick={() => setShowNutritionModal(true)}
                  className="group/info relative w-12 h-12 rounded-full overflow-hidden transition-all duration-500 hover:scale-110"
                  title={language === 'ar' ? 'معلومات كاملة' : 'Full information'}
                >
                  <div className="absolute inset-0 bg-gradient-to-br from-gold via-primary to-primary-dark"></div>
                  <div className="absolute inset-[1px] bg-white rounded-full flex items-center justify-center group-hover/info:bg-transparent transition-colors duration-500">
                    <svg className="w-5 h-5 text-primary group-hover/info:text-white transition-colors duration-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  </div>
                  <div className="absolute inset-0 bg-gradient-to-br from-gold/20 via-primary/20 to-primary-dark/20 opacity-0 group-hover/info:opacity-100 transition-opacity duration-500"></div>
                </button>
              </div>

              {/* Warning badges with luxury style */}
              {(item.spicyLevel > 0 || item.highSodium || item.containsCaffeine) && (
                <div className="flex gap-2 mt-4">
                  {item.spicyLevel > 0 && (
                    <span className="inline-flex items-center gap-1 bg-gradient-to-r from-red-50 to-orange-50 text-red-700 px-3 py-1.5 rounded-full text-xs font-medium border border-red-200/30">
                      {'🌶️'.repeat(item.spicyLevel)}
                    </span>
                  )}
                  {item.highSodium && (
                    <span className="inline-flex items-center gap-1.5 bg-gradient-to-r from-orange-50 to-amber-50 text-orange-700 px-3 py-1.5 rounded-full text-xs font-medium border border-orange-200/30">
                      <DietaryIcons type="highSodium" size="w-3 h-3" />
                      <span className="font-light">{language === 'ar' ? 'صوديوم' : 'Sodium'}</span>
                    </span>
                  )}
                  {item.containsCaffeine && (
                    <span className="inline-flex items-center gap-1.5 bg-gradient-to-r from-amber-50 to-yellow-50 text-amber-700 px-3 py-1.5 rounded-full text-xs font-medium border border-amber-200/30">
                      <DietaryIcons type="containsCaffeine" size="w-3 h-3" />
                      <span className="font-light">{language === 'ar' ? 'كافيين' : 'Caffeine'}</span>
                    </span>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Nutrition Modal */}
      <NutritionModal 
        item={item}
        isOpen={showNutritionModal}
        onClose={() => setShowNutritionModal(false)}
        language={language}
      />
    </>
  );
};

export default MenuItemCard;
