import React from 'react';
import AllergenIcons from './AllergenIcons';
import DietaryIcons, { dietaryInfo } from './DietaryIcons';

const NutritionModal = ({ item, isOpen, onClose, language }) => {
  if (!isOpen) return null;

  const formatLabel = (label, labelAr) => {
    return language === 'ar' ? labelAr : label;
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" onClick={onClose}>
      <div 
        className="bg-white rounded-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto"
        onClick={(e) => e.stopPropagation()}
        dir={language === 'ar' ? 'rtl' : 'ltr'}
      >
        {/* Header */}
        <div className="bg-gradient-to-r from-primary to-primary-light text-white p-6 rounded-t-2xl">
          <div className="flex justify-between items-start">
            <div>
              <h2 className="text-2xl font-playfair font-bold mb-2">
                {language === 'ar' && item.nameAr ? item.nameAr : item.name}
              </h2>
              <p className="text-sm opacity-90">
                {formatLabel('Nutritional Information', 'المعلومات الغذائية')}
              </p>
            </div>
            <button
              onClick={onClose}
              className="text-white hover:bg-white/20 rounded-full p-2 transition-colors"
            >
              <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6">
          {/* Basic Info */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="bg-gray-50 rounded-xl p-4">
              <h3 className="font-semibold text-primary mb-3">
                {formatLabel('Basic Information', 'المعلومات الأساسية')}
              </h3>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-600">{formatLabel('Price', 'السعر')}:</span>
                  <span className="font-semibold text-gold">{item.price}</span>
                </div>
                {item.priceWithoutVat && (
                  <div className="flex justify-between">
                    <span className="text-gray-600">{formatLabel('Price (excl. VAT)', 'السعر (بدون ضريبة)')}:</span>
                    <span>{item.priceWithoutVat}</span>
                  </div>
                )}
                {item.servingSize && (
                  <div className="flex justify-between">
                    <span className="text-gray-600">{formatLabel('Serving Size', 'حجم الحصة')}:</span>
                    <span>{item.servingSize}</span>
                  </div>
                )}
                {item.preparationTime && (
                  <div className="flex justify-between">
                    <span className="text-gray-600">{formatLabel('Preparation Time', 'وقت التحضير')}:</span>
                    <span>{item.preparationTime} {formatLabel('mins', 'دقيقة')}</span>
                  </div>
                )}
              </div>
            </div>

            <div className="bg-gray-50 rounded-xl p-4">
              <h3 className="font-semibold text-primary mb-3">
                {formatLabel('Nutritional Values', 'القيم الغذائية')}
              </h3>
              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-600">{formatLabel('Calories', 'السعرات الحرارية')}:</span>
                  <span className="font-semibold">{item.calories} cal</span>
                </div>
                {item.highSodium && (
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">{formatLabel('Sodium', 'الصوديوم')}:</span>
                    <span className="text-orange-500 font-semibold">
                      {formatLabel('High', 'عالي')} ⚠️
                    </span>
                  </div>
                )}
                {item.containsCaffeine && (
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">{formatLabel('Caffeine', 'الكافيين')}:</span>
                    <span className="text-amber-700">
                      {formatLabel('Contains', 'يحتوي')} ☕
                    </span>
                  </div>
                )}
                {item.spicyLevel > 0 && (
                  <div className="flex justify-between items-center">
                    <span className="text-gray-600">{formatLabel('Spicy Level', 'مستوى الحرارة')}:</span>
                    <span className="text-red-500">{'🌶️'.repeat(item.spicyLevel)}</span>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Exercise Info */}
          {(item.walkMinutes || item.runMinutes) && (
            <div className="bg-blue-50 rounded-xl p-4">
              <h3 className="font-semibold text-primary mb-3">
                {formatLabel('Exercise to Burn Calories', 'التمارين لحرق السعرات')}
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {item.walkMinutes && (
                  <div className="flex items-center gap-3">
                    <div className="bg-blue-100 p-3 rounded-full">
                      <svg className="w-6 h-6 text-blue-600" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M14.12,10H19V8.2H15.38L13.38,4.87C13.08,4.37 12.54,4.03 11.92,4.03C11.74,4.03 11.58,4.06 11.42,4.11L6,5.8V11H7.8V7.33L9.91,6.67L6,22H7.8L10.67,13.89L13,17V22H14.8V15.59L12.31,11.05L13.04,8.18M14,3.8C15,3.8 15.8,3 15.8,2C15.8,1 15,0.2 14,0.2C13,0.2 12.2,1 12.2,2C12.2,3 13,3.8 14,3.8Z"/>
                      </svg>
                    </div>
                    <div>
                      <p className="font-semibold">{formatLabel('Walking', 'المشي')}</p>
                      <p className="text-sm text-gray-600">{item.walkMinutes} {formatLabel('minutes', 'دقيقة')}</p>
                    </div>
                  </div>
                )}
                {item.runMinutes && (
                  <div className="flex items-center gap-3">
                    <div className="bg-blue-100 p-3 rounded-full">
                      <svg className="w-6 h-6 text-blue-600" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M13.5,5.5C14.59,5.5 15.5,4.58 15.5,3.5C15.5,2.38 14.59,1.5 13.5,1.5C12.39,1.5 11.5,2.38 11.5,3.5C11.5,4.58 12.39,5.5 13.5,5.5M9.89,19.38L10.89,15L13,17V23H15V15.5L12.89,13.5L13.5,10.5C14.79,12 16.79,13 19,13V11C17.09,11 15.5,10 14.69,8.58L13.69,7C13.29,6.38 12.69,6 12,6C11.69,6 11.5,6.08 11.19,6.08L6,8.28V13H8V9.58L9.79,8.88L8.19,17L3.29,16L2.89,18L9.89,19.38Z"/>
                      </svg>
                    </div>
                    <div>
                      <p className="font-semibold">{formatLabel('Running', 'الجري')}</p>
                      <p className="text-sm text-gray-600">{item.runMinutes} {formatLabel('minutes', 'دقيقة')}</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Dietary Info */}
          <div className="bg-gray-50 rounded-xl p-4">
            <h3 className="font-semibold text-primary mb-3">
              {formatLabel('Dietary Information', 'معلومات النظام الغذائي')}
            </h3>
            <div className="flex flex-wrap gap-2">
              {item.halal && (
                <span className="inline-flex items-center gap-1 bg-emerald-100 text-emerald-700 px-3 py-1 rounded-full text-sm font-medium">
                  <span>✓</span> {formatLabel('Halal', 'حلال')}
                </span>
              )}
              {item.glutenFree && (
                <span className="inline-flex items-center gap-1 bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm font-medium">
                  <span>✓</span> {formatLabel('Gluten Free', 'خالي من الجلوتين')}
                </span>
              )}
              {item.dairyFree && (
                <span className="inline-flex items-center gap-1 bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm font-medium">
                  <span>✓</span> {formatLabel('Dairy Free', 'خالي من الألبان')}
                </span>
              )}
              {item.nutFree && (
                <span className="inline-flex items-center gap-1 bg-purple-100 text-purple-700 px-3 py-1 rounded-full text-sm font-medium">
                  <span>✓</span> {formatLabel('Nut Free', 'خالي من المكسرات')}
                </span>
              )}
              {item.vegetarian && (
                <span className="inline-flex items-center gap-1 bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm font-medium">
                  <span>✓</span> {formatLabel('Vegetarian', 'نباتي')}
                </span>
              )}
              {item.vegan && (
                <span className="inline-flex items-center gap-1 bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm font-medium">
                  <span>✓</span> {formatLabel('Vegan', 'نباتي صرف')}
                </span>
              )}
            </div>
          </div>

          {/* Allergens */}
          {item.allergens && item.allergens.length > 0 && (
            <div className="bg-red-50 rounded-xl p-4">
              <h3 className="font-semibold text-red-700 mb-3">
                {formatLabel('Allergen Information', 'معلومات المواد المسببة للحساسية')}
              </h3>
              <div className="flex items-center gap-2">
                <AllergenIcons allergens={item.allergens} size="w-8 h-8" showLabels={true} />
              </div>
            </div>
          )}

          {/* Description */}
          <div className="border-t pt-4">
            <h3 className="font-semibold text-primary mb-2">
              {formatLabel('Description', 'الوصف')}
            </h3>
            <p className="text-gray-600 text-sm leading-relaxed">
              {language === 'ar' && item.descriptionAr ? item.descriptionAr : item.description}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NutritionModal;
