import React from 'react';

const CategoryFilter = ({ categories, activeCategory, onCategoryChange }) => {
  return (
    <nav className="sticky top-0 z-40 bg-white/95 backdrop-blur-lg shadow-lg">
      <div className="container mx-auto px-4 py-6">
        <div className="flex justify-center items-center flex-wrap gap-3">
          <button
            onClick={() => onCategoryChange('all')}
            className={`px-6 py-2 rounded-full font-medium text-sm uppercase tracking-wider transition-all duration-300 ${
              activeCategory === 'all'
                ? 'bg-gradient-to-r from-primary to-primary-light text-white shadow-lg transform -translate-y-0.5'
                : 'border-2 border-primary text-primary hover:bg-primary hover:text-white'
            }`}
          >
            All
          </button>
          {categories.map((category) => (
            <button
              key={category.value}
              onClick={() => onCategoryChange(category.value)}
              className={`px-6 py-2 rounded-full font-medium text-sm uppercase tracking-wider transition-all duration-300 ${
                activeCategory === category.value
                  ? 'bg-gradient-to-r from-primary to-primary-light text-white shadow-lg transform -translate-y-0.5'
                  : 'border-2 border-primary text-primary hover:bg-primary hover:text-white'
              }`}
            >
              {category.label}
            </button>
          ))}
        </div>
      </div>

      {/* Decorative line pattern */}
      <div className="absolute bottom-0 left-0 right-0 h-px bg-gradient-to-r from-transparent via-primary/20 to-transparent"></div>
    </nav>
  );
};

export default CategoryFilter;
