import React, { useState, useEffect } from 'react';
import LuxuryCategoryFilter from '../components/LuxuryCategoryFilter';
import LuxuryMenuCard from '../components/LuxuryMenuCard';
import LuxuryMobileCard from '../components/LuxuryMobileCard';
import { menuAPI } from '../services/api';
import DOMPurify from 'dompurify';

const RestaurantMenu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [language, setLanguage] = useState('en');
  const [settings, setSettings] = useState(null);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    fetchData();

    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [itemsData, categoriesData, settingsData] = await Promise.all([
        menuAPI.getMenuItems(),
        menuAPI.getCategories(),
        menuAPI.getSettings()
      ]);
      setMenuItems(itemsData);
      setCategories(categoriesData.categories);
      setSettings(settingsData);
    } catch (err) {
      setError('Failed to load menu. Please try again later.');
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  const filteredItems = activeCategory === 'all' 
    ? menuItems 
    : menuItems.filter(item => item.category === activeCategory);

  const formatCategory = (category) => {
    const categoryData = categories.find(cat => cat.value === category);
    if (!categoryData) return category;
    return language === 'ar' && categoryData.labelAr ? categoryData.labelAr : categoryData.label;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="inline-flex items-center justify-center w-16 h-16 mb-4">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gray-900"></div>
          </div>
          <p className="text-gray-600">Loading menu...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <p className="text-red-500 mb-4">{error}</p>
          <button 
            onClick={fetchData} 
            className="px-6 py-2 bg-gray-900 text-white rounded-full hover:bg-gray-800 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50" dir={language === 'ar' ? 'rtl' : 'ltr'}>
      {/* Top Navigation Bar */}
      <nav className="bg-white shadow-lg fixed top-0 left-0 right-0 z-50">
        <div className="container mx-auto px-4">
          <div className="flex items-center justify-between h-20">
            {/* Left Side - Website Link */}
            <div className="flex-1">
              <a
                href="https://entrecote.sa"
                className="inline-flex items-center gap-2 text-gray-700 hover:text-gray-900 transition-colors group"
              >
                <div className="w-8 h-8 rounded-full bg-gray-100 group-hover:bg-gray-200 flex items-center justify-center transition-colors">
                  <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
                  </svg>
                </div>
                <span className="hidden sm:inline text-sm font-medium">
                  {language === 'ar' ? 'الموقع الرئيسي' : 'Main Website'}
                </span>
              </a>
            </div>

            {/* Center - Logo */}
            <div className="flex-shrink-0 mx-4">
              <img 
                src="/logo.png" 
                alt="Entrecôte Café de Paris" 
                className="h-14 md:h-16 object-contain"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.parentElement.innerHTML = '<div class="text-2xl font-bold text-gray-900">Entrecôte</div>';
                }}
              />
            </div>

            {/* Right Side - Actions */}
            <div className="flex-1 flex items-center justify-end gap-2 md:gap-3">
              {/* Instagram Button */}
              <a
                href="https://instagram.com/entrecote.sa"
                target="_blank"
                rel="noopener noreferrer"
                className="inline-flex items-center gap-2 px-3 md:px-4 py-2 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-full hover:shadow-lg transition-all duration-300 group"
              >
                <svg className="w-5 h-5 flex-shrink-0" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zM5.838 12a6.162 6.162 0 1112.324 0 6.162 6.162 0 01-12.324 0zM12 16a4 4 0 110-8 4 4 0 010 8zm4.965-10.405a1.44 1.44 0 112.881.001 1.44 1.44 0 01-2.881-.001z"/>
                </svg>
                <span className="text-sm font-medium hidden sm:inline">
                  entrecote.sa
                </span>
              </a>

              {/* Language Switcher */}
              <button
                onClick={() => setLanguage(language === 'en' ? 'ar' : 'en')}
                className="inline-flex items-center gap-2 px-3 md:px-4 py-2 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-full transition-colors group"
              >
                <svg className="w-5 h-5 flex-shrink-0" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12.87 15.07l-2.54-2.51.03-.03c1.74-1.94 2.98-4.17 3.71-6.53H17V4h-7V2H8v2H1v1.99h11.17C11.5 7.92 10.44 9.75 9 11.35 8.07 10.32 7.3 9.19 6.69 8h-2c.73 1.63 1.73 3.17 2.98 4.56l-5.09 5.02L4 19l5-5 3.11 3.11.76-2.04zM18.5 10h-2L12 22h2l1.12-3h4.75L21 22h2l-4.5-12zm-2.62 7l1.62-4.33L19.12 17h-3.24z"/>
                </svg>
                <span className="text-sm font-medium">
                  {language === 'en' ? 'عربي' : 'EN'}
                </span>
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="bg-gradient-to-br from-gray-900 to-gray-700 text-white pt-28 pb-12">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">
            {language === 'ar' ? 'قائمة الطعام' : 'Our Menu'}
          </h1>
          <p className="text-lg md:text-xl text-gray-200 max-w-2xl mx-auto">
            {language === 'ar' 
              ? 'اكتشف تشكيلتنا الرائعة من الأطباق الفرنسية الأصيلة'
              : 'Discover our exquisite selection of authentic French cuisine'
            }
          </p>
        </div>
      </div>

      {/* Category Filter */}
      <div className="sticky top-20 z-30">
        <LuxuryCategoryFilter 
          categories={categories}
          activeCategory={activeCategory}
          onCategoryChange={setActiveCategory}
          language={language}
        />
      </div>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-8">
        {isMobile ? (
          // Mobile Layout
          <div className="space-y-4">
            {filteredItems.map((item) => (
              <LuxuryMobileCard 
                key={item.id}
                item={item}
                language={language}
                formatCategory={formatCategory}
                categories={categories}
              />
            ))}
          </div>
        ) : (
          // Desktop Layout
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredItems.map((item) => (
              <LuxuryMenuCard 
                key={item.id}
                item={item}
                language={language}
                formatCategory={formatCategory}
                categories={categories}
              />
            ))}
          </div>
        )}

        {filteredItems.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">
              {language === 'ar' ? 'لا توجد عناصر في هذه الفئة.' : 'No items found in this category.'}
            </p>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-[#00594f] text-white mt-16">
        <div className="container mx-auto px-4 py-8">
          {/* Restaurant Info */}
          <div className="text-center">
            <img 
              src="/logo.png" 
              alt="Entrecôte Café de Paris" 
              className="h-12 mb-4 mx-auto brightness-0 invert"
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.parentElement.innerHTML = '<div class="text-2xl font-bold text-white mb-4">Entrecôte</div>' + e.target.parentElement.innerHTML;
              }}
            />
            <p className="text-white/80 max-w-2xl mx-auto mb-6">
              {language === 'ar' 
                ? 'تجربة طعام فرنسية أصيلة في قلب المملكة'
                : 'Authentic French dining experience in the heart of the Kingdom'
              }
            </p>
          </div>

          {/* Custom Footer Text */}
          {settings && settings.footerEnabled && (settings.footerTextEn || settings.footerTextAr) && (
            <div className="mb-6">
              <div 
                className="text-center text-white/70 prose prose-sm max-w-none prose-invert"
                dangerouslySetInnerHTML={{ 
                  __html: DOMPurify.sanitize(language === 'ar' && settings.footerTextAr ? settings.footerTextAr : settings.footerTextEn) 
                }}
              />
            </div>
          )}

          {/* Copyright */}
          <div className="pt-6 border-t border-white/20 text-center text-white/60 text-sm">
            <p>© 2025 Entrecôte Café de Paris. {language === 'ar' ? 'جميع الحقوق محفوظة' : 'All rights reserved'}.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default RestaurantMenu;
