import React, { useState } from 'react';
import AllergenIcons from './AllergenIcons';
import NutritionModal from './NutritionModal';

const MenuItemCard = ({ item, language, formatCategory }) => {
  const [showNutritionModal, setShowNutritionModal] = useState(false);

  return (
    <>
      <div className="menu-item-card">
        <div className="group bg-white rounded-2xl shadow-lg transition-all duration-300 hover:shadow-2xl hover:-translate-y-1 border border-transparent hover:border-primary/20 relative overflow-hidden">
          {/* Top gradient line */}
          <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-primary via-gold to-primary transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300"></div>

          {/* Image */}
          {item.image && (
            <div className="relative h-48 overflow-hidden rounded-t-2xl">
              <img 
                src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${item.image}`}
                alt={language === 'ar' && item.nameAr ? item.nameAr : item.name}
                className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"
              />
              {/* Category badge on image */}
              <div className="absolute top-3 left-3">
                <span className="inline-block bg-primary/90 backdrop-blur text-white px-3 py-1 rounded-full text-xs font-medium uppercase tracking-wider">
                  {formatCategory(item.category)}
                </span>
              </div>
            </div>
          )}

          <div className="p-6">
            {/* Header */}
            <div className="flex justify-between items-start mb-3">
              <h3 className="text-xl font-playfair font-bold text-primary flex-1 mr-4">
                {language === 'ar' && item.nameAr ? item.nameAr : item.name}
              </h3>
              <div className="text-right">
                <span className="text-2xl font-bold bg-gradient-to-r from-gold via-primary to-gold bg-clip-text text-transparent">
                  {item.price}
                </span>
                {item.priceWithoutVat && (
                  <p className="text-xs text-gray-500 mt-1">
                    {language === 'ar' ? 'ÿ®ÿØŸàŸÜ ÿ∂ÿ±Ÿäÿ®ÿ©: ' : 'excl. VAT: '}{item.priceWithoutVat}
                  </p>
                )}
              </div>
            </div>

            {/* Description */}
            <p className="text-gray-600 text-sm leading-relaxed mb-4 line-clamp-2">
              {language === 'ar' && item.descriptionAr ? item.descriptionAr : item.description}
            </p>

            {/* Quick info badges */}
            <div className="flex flex-wrap gap-2 mb-4">
              {item.halal && (
                <span className="inline-flex items-center gap-1 bg-emerald-100 text-emerald-700 px-2 py-1 rounded-lg text-xs font-medium">
                  ‚úì ÿ≠ŸÑÿßŸÑ
                </span>
              )}
              {item.calories && (
                <span className="inline-flex items-center gap-1 bg-blue-100 text-blue-700 px-2 py-1 rounded-lg text-xs font-medium">
                  {item.calories} cal
                </span>
              )}
              {item.spicyLevel > 0 && (
                <span className="inline-flex items-center gap-1 bg-red-100 text-red-700 px-2 py-1 rounded-lg text-xs font-medium">
                  {'üå∂Ô∏è'.repeat(item.spicyLevel)}
                </span>
              )}
              {(item.walkMinutes || item.runMinutes) && (
                <span className="inline-flex items-center gap-1 bg-purple-100 text-purple-700 px-2 py-1 rounded-lg text-xs font-medium">
                  üèÉ {item.walkMinutes || item.runMinutes} min
                </span>
              )}
              {item.preparationTime && (
                <span className="inline-flex items-center gap-1 bg-gray-100 text-gray-700 px-2 py-1 rounded-lg text-xs font-medium">
                  ‚è±Ô∏è {item.preparationTime} min
                </span>
              )}
            </div>

            {/* Dietary tags and allergens row */}
            <div className="flex items-center justify-between pt-4 border-t border-gray-100">
              <div className="flex gap-2 flex-wrap items-center">
                {/* Dietary tags */}
                {item.glutenFree && <span className="dietary-tag bg-green-600">GF</span>}
                {item.dairyFree && <span className="dietary-tag bg-blue-600">DF</span>}
                {item.nutFree && <span className="dietary-tag bg-purple-600">NF</span>}
                {item.vegetarian && <span className="dietary-tag bg-green-600">V</span>}
                {item.vegan && <span className="dietary-tag bg-green-700">VG</span>}

                {/* Allergen icons */}
                {item.allergens && item.allergens.length > 0 && (
                  <AllergenIcons allergens={item.allergens} size="w-4 h-4" />
                )}
              </div>

              {/* Info button */}
              <button
                onClick={() => setShowNutritionModal(true)}
                className="flex items-center justify-center w-8 h-8 rounded-full bg-primary/10 text-primary hover:bg-primary hover:text-white transition-all duration-300"
                title={language === 'ar' ? 'ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ∫ÿ∞ÿßÿ¶Ÿäÿ© ŸÉÿßŸÖŸÑÿ©' : 'Full nutritional information'}
              >
                <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </button>
            </div>

            {/* Special indicators */}
            {(item.highSodium || item.containsCaffeine) && (
              <div className="mt-3 flex gap-2">
                {item.highSodium && (
                  <span className="text-xs text-orange-600 flex items-center gap-1">
                    <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                    {language === 'ar' ? 'ÿµŸàÿØŸäŸàŸÖ ÿπÿßŸÑŸä' : 'High Sodium'}
                  </span>
                )}
                {item.containsCaffeine && (
                  <span className="text-xs text-amber-700 flex items-center gap-1">
                    ‚òï {language === 'ar' ? 'Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ŸÉÿßŸÅŸäŸäŸÜ' : 'Contains Caffeine'}
                  </span>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Nutrition Modal */}
      <NutritionModal 
        item={item}
        isOpen={showNutritionModal}
        onClose={() => setShowNutritionModal(false)}
        language={language}
      />
    </>
  );
};

export default MenuItemCard;
