import React, { useState, useEffect } from 'react';
import CategoryFilter from '../components/CategoryFilter';
import { menuAPI } from '../services/api';
import AllergenIcons from '../components/AllergenIcons';

const EmbeddedMenu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [language, setLanguage] = useState('en'); // 'en' or 'ar'

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [itemsData, categoriesData] = await Promise.all([
        menuAPI.getMenuItems(),
        menuAPI.getCategories()
      ]);
      setMenuItems(itemsData);
      setCategories(categoriesData.categories);
    } catch (err) {
      setError('Failed to load menu. Please try again later.');
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  const filteredItems = activeCategory === 'all' 
    ? menuItems 
    : menuItems.filter(item => item.category === activeCategory);

  const formatCategory = (category) => {
    const categoryData = categories.find(cat => cat.value === category);
    if (!categoryData) return category;
    return language === 'ar' && categoryData.labelAr ? categoryData.labelAr : categoryData.label;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-cream">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-primary mx-auto mb-4"></div>
          <p className="text-primary">Loading menu...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-cream">
        <div className="text-center">
          <p className="text-red-500 mb-4">{error}</p>
          <button onClick={fetchData} className="btn-primary">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-cream min-h-screen" dir={language === 'ar' ? 'rtl' : 'ltr'}>
      {/* Language Switcher */}
      <div className="bg-white shadow-sm sticky top-0 z-50">
        <div className="container mx-auto px-4 py-3 flex justify-end">
          <button
            onClick={() => setLanguage(language === 'en' ? 'ar' : 'en')}
            className="flex items-center gap-2 px-4 py-2 rounded-lg border border-primary text-primary hover:bg-primary hover:text-white transition-all duration-300"
          >
            <svg className="w-5 h-5" viewBox="0 0 24 24" fill="currentColor">
              <path d="M12.87 15.07l-2.54-2.51.03-.03c1.74-1.94 2.98-4.17 3.71-6.53H17V4h-7V2H8v2H1v1.99h11.17C11.5 7.92 10.44 9.75 9 11.35 8.07 10.32 7.3 9.19 6.69 8h-2c.73 1.63 1.73 3.17 2.98 4.56l-5.09 5.02L4 19l5-5 3.11 3.11.76-2.04zM18.5 10h-2L12 22h2l1.12-3h4.75L21 22h2l-4.5-12zm-2.62 7l1.62-4.33L19.12 17h-3.24z"/>
            </svg>
            {language === 'en' ? 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©' : 'English'}
          </button>
        </div>
      </div>

      <CategoryFilter 
        categories={categories}
        activeCategory={activeCategory}
        onCategoryChange={setActiveCategory}
      />

      <main className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredItems.map((item) => (
            <div key={item.id} className="menu-item-card">
              <div className="group bg-white rounded-2xl p-6 shadow-lg transition-all duration-300 hover:shadow-2xl hover:-translate-y-1 border border-transparent hover:border-primary/20 relative overflow-hidden">
                {/* Top gradient line */}
                <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-primary via-gold to-primary transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300"></div>

                {/* Image */}
                {item.image && (
                  <img 
                    src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${item.image}`}
                    alt={language === 'ar' && item.nameAr ? item.nameAr : item.name}
                    className="w-full h-48 object-cover rounded-lg mb-4"
                  />
                )}

                {/* Header */}
                <div className="flex justify-between items-start mb-3">
                  <h3 className="text-xl font-playfair font-bold text-primary flex-1 mr-4">
                    {language === 'ar' && item.nameAr ? item.nameAr : item.name}
                  </h3>
                  <div className="text-right">
                    <span className="text-xl font-semibold text-gold whitespace-nowrap animate-shimmer bg-gradient-to-r from-gold via-primary to-gold bg-[length:200%_auto] bg-clip-text text-transparent">
                      {item.price}
                    </span>
                    {item.priceWithoutVat && (
                      <p className="text-xs text-gray-500 mt-1">
                        {language === 'ar' ? 'ÿ®ÿØŸàŸÜ ÿ∂ÿ±Ÿäÿ®ÿ©: ' : 'Without VAT: '}{item.priceWithoutVat}
                      </p>
                    )}
                  </div>
                </div>

                {/* Description */}
                <p className="text-primary-dark/80 text-sm leading-relaxed mb-4">
                  {language === 'ar' && item.descriptionAr ? item.descriptionAr : item.description}
                </p>

                {/* Footer */}
                <div className="flex justify-between items-center flex-wrap gap-3">
                  <span className="inline-block bg-gradient-to-r from-primary to-primary-light text-white px-4 py-1 rounded-full text-xs font-medium uppercase tracking-wider shadow-md">
                    {formatCategory(item.category)}
                  </span>

                  {/* Dietary tags */}
                  <div className="flex gap-2 flex-wrap">
                    {item.halal && <span className="dietary-tag bg-emerald-600">ÿ≠ŸÑÿßŸÑ</span>}
                    {item.glutenFree && <span className="dietary-tag">GF</span>}
                    {item.dairyFree && <span className="dietary-tag">DF</span>}
                    {item.nutFree && <span className="dietary-tag">NF</span>}
                    {item.vegetarian && <span className="dietary-tag">V</span>}
                    {item.vegan && <span className="dietary-tag">VG</span>}
                  </div>
                </div>

                {/* Info badges */}
                {(item.calories || item.highSodium || item.containsCaffeine || item.walkMinutes || item.runMinutes || (item.allergens && item.allergens.length > 0) || item.spicyLevel > 0 || item.preparationTime || item.servingSize) && (
                  <div className="mt-4 pt-4 border-t border-primary/10 flex flex-wrap gap-3 text-xs">
                    {item.calories && (
                      <div className="flex items-center gap-1 text-secondary">
                        <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                          <path d="M12,6A6,6 0 0,0 6,12A6,6 0 0,0 12,18A6,6 0 0,0 18,12A6,6 0 0,0 12,6M12,16A4,4 0 0,1 8,12A4,4 0 0,1 12,8A4,4 0 0,1 16,12A4,4 0 0,1 12,16Z"/>
                        </svg>
                        <span>{item.calories} {language === 'ar' ? 'ÿ≥ÿπÿ±ÿ©' : 'cal'}</span>
                      </div>
                    )}

                    {item.highSodium && (
                      <div className="flex items-center gap-1 text-orange-500" title={language === 'ar' ? 'ŸÜÿ≥ÿ®ÿ© ÿµŸàÿØŸäŸàŸÖ ÿπÿßŸÑŸäÿ©' : 'High Sodium'}>
                        <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                          <path d="M2,12H4V14H2V12M20,6H22V8H20V6M20,12H22V14H20V12M20,18H22V20H20V18M6,4V6H8V4H6M6,20V22H8V20H6M12,2V4H14V2H12M12,20V22H14V20H12M18,4V6H20V4H18M2,6H4V8H2V6M2,18H4V20H2V18M8,14A2,2 0 0,1 6,12A2,2 0 0,1 8,10H11V8A2,2 0 0,1 13,6A2,2 0 0,1 15,8V10H18A2,2 0 0,1 20,12A2,2 0 0,1 18,14H15V16A2,2 0 0,1 13,18A2,2 0 0,1 11,16V14H8Z"/>
                        </svg>
                        <span>{language === 'ar' ? 'ÿµŸàÿØŸäŸàŸÖ ÿπÿßŸÑŸä' : 'High Sodium'}</span>
                      </div>
                    )}

                    {item.containsCaffeine && (
                      <div className="flex items-center gap-1 text-amber-700" title={language === 'ar' ? 'Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ŸÉÿßŸÅŸäŸäŸÜ' : 'Contains Caffeine'}>
                        <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                          <path d="M2,21V19H20V21H2M20,8V5H18V8H20M20,3A2,2 0 0,1 22,5V8A2,2 0 0,1 20,10H18V13A4,4 0 0,1 14,17H8A4,4 0 0,1 4,13V3H20M16,5H6V13A2,2 0 0,0 8,15H14A2,2 0 0,0 16,13V5Z"/>
                        </svg>
                        <span>{language === 'ar' ? 'ŸÉÿßŸÅŸäŸäŸÜ' : 'Caffeine'}</span>
                      </div>
                    )}

                    {item.spicyLevel > 0 && (
                      <div className="flex items-center gap-1 text-red-500">
                        <span>{'üå∂Ô∏è'.repeat(item.spicyLevel)}</span>
                      </div>
                    )}

                    {(item.walkMinutes || item.runMinutes) && (
                      <div className="flex items-center gap-3 text-primary">
                        {item.walkMinutes && (
                          <div className="flex items-center gap-1">
                            <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                              <path d="M14.12,10H19V8.2H15.38L13.38,4.87C13.08,4.37 12.54,4.03 11.92,4.03C11.74,4.03 11.58,4.06 11.42,4.11L6,5.8V11H7.8V7.33L9.91,6.67L6,22H7.8L10.67,13.89L13,17V22H14.8V15.59L12.31,11.05L13.04,8.18M14,3.8C15,3.8 15.8,3 15.8,2C15.8,1 15,0.2 14,0.2C13,0.2 12.2,1 12.2,2C12.2,3 13,3.8 14,3.8Z"/>
                            </svg>
                            <span>{item.walkMinutes} {language === 'ar' ? 'ÿØŸÇŸäŸÇÿ©' : 'min'}</span>
                          </div>
                        )}
                        {item.runMinutes && (
                          <div className="flex items-center gap-1">
                            <svg className="w-4 h-4" viewBox="0 0 24 24" fill="currentColor">
                              <path d="M13.5,5.5C14.59,5.5 15.5,4.58 15.5,3.5C15.5,2.38 14.59,1.5 13.5,1.5C12.39,1.5 11.5,2.38 11.5,3.5C11.5,4.58 12.39,5.5 13.5,5.5M9.89,19.38L10.89,15L13,17V23H15V15.5L12.89,13.5L13.5,10.5C14.79,12 16.79,13 19,13V11C17.09,11 15.5,10 14.69,8.58L13.69,7C13.29,6.38 12.69,6 12,6C11.69,6 11.5,6.08 11.19,6.08L6,8.28V13H8V9.58L9.79,8.88L8.19,17L3.29,16L2.89,18L9.89,19.38Z"/>
                            </svg>
                            <span>{item.runMinutes} {language === 'ar' ? 'ÿØŸÇŸäŸÇÿ©' : 'min'}</span>
                          </div>
                        )}
                      </div>
                    )}

                    {item.preparationTime && (
                      <div className="flex items-center gap-1 text-gray-600">
                        <span>‚è±Ô∏è</span>
                        <span>{item.preparationTime} {language === 'ar' ? 'ÿØŸÇŸäŸÇÿ©' : 'mins'}</span>
                      </div>
                    )}

                    {item.servingSize && (
                      <div className="flex items-center gap-1 text-gray-600">
                        <span>üçΩÔ∏è</span>
                        <span>{item.servingSize}</span>
                      </div>
                    )}

                    {item.allergens && item.allergens.length > 0 && (
                      <div className="flex items-center gap-1">
                        <AllergenIcons allergens={item.allergens} size="w-4 h-4" />
                      </div>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {filteredItems.length === 0 && (
          <div className="text-center py-12">
            <p className="text-primary-dark text-lg">
              {language === 'ar' ? 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿπŸÜÿßÿµÿ± ŸÅŸä Ÿáÿ∞Ÿá ÿßŸÑŸÅÿ¶ÿ©.' : 'No items found in this category.'}
            </p>
          </div>
        )}
      </main>
    </div>
  );
};

export default EmbeddedMenu;
