import React, { useState } from 'react';
import AllergenIcons from './AllergenIcons';
import DietaryIcons from './DietaryIcons';
import NutritionModal from './NutritionModal';

const MenuItemCard = ({ item, language, formatCategory }) => {
  const [showNutritionModal, setShowNutritionModal] = useState(false);

  return (
    <>
      <div className="menu-item-card group">
        <div className="relative bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-all duration-500 border border-gray-100 hover:border-gold/40">
          {/* Luxury shimmer effect */}
          <div className="absolute inset-0 bg-gradient-to-br from-gold/[0.02] via-transparent to-primary/[0.02] opacity-0 group-hover:opacity-100 transition-opacity duration-700"></div>

          {/* Compact image section */}
          {item.image ? (
            <div className="relative h-40 overflow-hidden">
              <img 
                src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${item.image}`}
                alt={language === 'ar' && item.nameAr ? item.nameAr : item.name}
                className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
              />

              {/* Elegant gradient overlay */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent to-transparent"></div>

              {/* Price overlay - bottom right */}
              <div className="absolute bottom-3 right-3">
                <div className="bg-black/60 backdrop-blur-md rounded-lg px-3 py-1.5">
                  <span className="text-lg font-light text-gold">
                    {item.price}
                  </span>
                </div>
              </div>

              {/* Category tag - top left */}
              <div className="absolute top-3 left-3">
                <span className="inline-block bg-white/90 backdrop-blur-sm text-primary px-3 py-1 rounded-full text-[10px] font-medium tracking-wider uppercase">
                  {formatCategory(item.category)}
                </span>
              </div>
            </div>
          ) : (
            <div className="relative h-24 bg-gradient-to-br from-gray-50 to-gray-100 flex items-center justify-between px-4">
              <span className="text-[10px] font-medium tracking-wider uppercase text-primary/70">
                {formatCategory(item.category)}
              </span>
              <span className="text-xl font-light text-primary">
                {item.price}
              </span>
            </div>
          )}

          {/* Compact content section */}
          <div className="p-4">
            {/* Title and essential info */}
            <div className="mb-2">
              <h3 className="text-lg font-medium text-gray-800 leading-tight mb-1 group-hover:text-primary transition-colors duration-300">
                {language === 'ar' && item.nameAr ? item.nameAr : item.name}
              </h3>
              <p className="text-xs text-gray-600 line-clamp-1">
                {language === 'ar' && item.descriptionAr ? item.descriptionAr : item.description}
              </p>
            </div>

            {/* Quick info row */}
            <div className="flex items-center justify-between mb-3">
              {/* Calories and exercise */}
              <div className="flex items-center gap-3 text-[10px]">
                {item.calories && (
                  <div className="flex items-center gap-1 text-primary">
                    <div className="w-5 h-5 rounded-full bg-primary/10 flex items-center justify-center">
                      <span className="text-[8px] font-bold">CAL</span>
                    </div>
                    <span className="font-medium">{item.calories}</span>
                  </div>
                )}
                {(item.walkMinutes || item.runMinutes) && (
                  <div className="flex items-center gap-1 text-gold">
                    <div className="w-5 h-5 rounded-full bg-gold/10 flex items-center justify-center">
                      <span className="text-[8px]">üèÉ</span>
                    </div>
                    <span className="font-medium">{item.walkMinutes || item.runMinutes}m</span>
                  </div>
                )}
              </div>

              {/* Spicy indicator */}
              {item.spicyLevel > 0 && (
                <div className="flex">
                  {[...Array(3)].map((_, i) => (
                    <span key={i} className={`text-xs ${i < item.spicyLevel ? 'text-red-500' : 'text-gray-300'}`}>
                      üå∂
                    </span>
                  ))}
                </div>
              )}
            </div>

            {/* Dietary badges - ultra compact */}
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-1">
                {/* Dietary icons */}
                <div className="flex -space-x-1">
                  {item.halal && (
                    <div className="w-6 h-6 rounded-full bg-emerald-100 border-2 border-white flex items-center justify-center" 
                         title={language === 'ar' ? 'ÿ≠ŸÑÿßŸÑ' : 'Halal'}>
                      <span className="text-[8px] font-bold text-emerald-700">H</span>
                    </div>
                  )}
                  {item.vegetarian && (
                    <div className="w-6 h-6 rounded-full bg-green-100 border-2 border-white flex items-center justify-center" 
                         title={language === 'ar' ? 'ŸÜÿ®ÿßÿ™Ÿä' : 'Vegetarian'}>
                      <span className="text-[8px] font-bold text-green-700">V</span>
                    </div>
                  )}
                  {item.vegan && (
                    <div className="w-6 h-6 rounded-full bg-lime-100 border-2 border-white flex items-center justify-center" 
                         title={language === 'ar' ? 'ŸÜÿ®ÿßÿ™Ÿä ÿµÿ±ŸÅ' : 'Vegan'}>
                      <span className="text-[8px] font-bold text-lime-700">VG</span>
                    </div>
                  )}
                  {item.glutenFree && (
                    <div className="w-6 h-6 rounded-full bg-amber-100 border-2 border-white flex items-center justify-center" 
                         title={language === 'ar' ? 'ÿÆÿßŸÑŸä ŸÖŸÜ ÿßŸÑÿ¨ŸÑŸàÿ™ŸäŸÜ' : 'Gluten Free'}>
                      <span className="text-[8px] font-bold text-amber-700">GF</span>
                    </div>
                  )}
                </div>

                {/* Allergen warning */}
                {item.allergens && item.allergens.length > 0 && (
                  <div className="ml-2 flex items-center">
                    <div className="w-6 h-6 rounded-full bg-red-500 text-white flex items-center justify-center animate-pulse">
                      <span className="text-[10px] font-bold">!</span>
                    </div>
                  </div>
                )}

                {/* Warnings */}
                {(item.highSodium || item.containsCaffeine) && (
                  <div className="ml-1 flex gap-1">
                    {item.highSodium && (
                      <div className="w-5 h-5 rounded bg-orange-100 flex items-center justify-center" 
                           title={language === 'ar' ? 'ÿµŸàÿØŸäŸàŸÖ ÿπÿßŸÑŸä' : 'High Sodium'}>
                        <span className="text-[10px]">üßÇ</span>
                      </div>
                    )}
                    {item.containsCaffeine && (
                      <div className="w-5 h-5 rounded bg-amber-100 flex items-center justify-center" 
                           title={language === 'ar' ? 'ŸÉÿßŸÅŸäŸäŸÜ' : 'Caffeine'}>
                        <span className="text-[10px]">‚òï</span>
                      </div>
                    )}
                  </div>
                )}
              </div>

              {/* Luxury info button */}
              <button
                onClick={() => setShowNutritionModal(true)}
                className="w-8 h-8 rounded-full bg-gradient-to-br from-gold/20 to-primary/20 hover:from-gold hover:to-primary text-primary hover:text-white transition-all duration-300 flex items-center justify-center group/btn"
                title={language === 'ar' ? 'ÿßŸÑŸÖÿ≤ŸäÿØ' : 'More info'}
              >
                <svg className="w-4 h-4 transition-transform duration-300 group-hover/btn:scale-110" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
              </button>
            </div>
          </div>

          {/* Luxury accent line */}
          <div className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-transparent via-gold/50 to-transparent transform scale-x-0 group-hover:scale-x-100 transition-transform duration-500"></div>
        </div>
      </div>

      {/* Nutrition Modal */}
      <NutritionModal 
        item={item}
        isOpen={showNutritionModal}
        onClose={() => setShowNutritionModal(false)}
        language={language}
      />
    </>
  );
};

export default MenuItemCard;
