import React, { useState } from 'react';
import AllergenIcons from './AllergenIcons';
import DietaryIcons from './DietaryIcons';
import NutritionModal from './NutritionModal';

const MenuItemCard = ({ item, language, formatCategory }) => {
  const [showNutritionModal, setShowNutritionModal] = useState(false);

  return (
    <>
      <div className="menu-item-card">
        <div className="group bg-white rounded-3xl shadow-xl transition-all duration-500 hover:shadow-2xl hover:-translate-y-2 relative overflow-hidden border border-gold/20">
          {/* Luxury gradient overlay */}
          <div className="absolute inset-0 bg-gradient-to-br from-gold/5 via-transparent to-primary/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>

          {/* Top accent */}
          <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-gold via-primary to-gold"></div>

          {/* Image section */}
          {item.image ? (
            <div className="relative h-56 overflow-hidden">
              <img 
                src={`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}${item.image}`}
                alt={language === 'ar' && item.nameAr ? item.nameAr : item.name}
                className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
              />
              {/* Gradient overlay on image */}
              <div className="absolute inset-0 bg-gradient-to-t from-black/40 via-transparent to-transparent"></div>

              {/* Category badge */}
              <div className="absolute top-4 left-4">
                <span className="inline-block bg-white/90 backdrop-blur-md text-primary px-4 py-2 rounded-full text-xs font-semibold uppercase tracking-wider shadow-lg">
                  {formatCategory(item.category)}
                </span>
              </div>

              {/* Price badge on image */}
              <div className="absolute bottom-4 right-4">
                <div className="bg-black/70 backdrop-blur-md rounded-2xl px-4 py-2 shadow-xl">
                  <span className="text-2xl font-bold text-gold">
                    {item.price}
                  </span>
                  {item.priceWithoutVat && (
                    <p className="text-xs text-gray-300 mt-0.5">
                      {language === 'ar' ? 'بدون ضريبة' : 'excl. VAT'}
                    </p>
                  )}
                </div>
              </div>
            </div>
          ) : (
            <div className="relative h-32 bg-gradient-to-br from-primary/10 via-gold/10 to-primary/10 flex items-center justify-center">
              <div className="text-center">
                <span className="inline-block bg-white/80 backdrop-blur text-primary px-4 py-2 rounded-full text-xs font-semibold uppercase tracking-wider shadow-md mb-2">
                  {formatCategory(item.category)}
                </span>
                <div className="text-2xl font-bold text-primary">
                  {item.price}
                </div>
              </div>
            </div>
          )}

          <div className="p-6 relative">
            {/* Header */}
            <div className="mb-3">
              <h3 className="text-xl font-playfair font-bold text-primary leading-tight">
                {language === 'ar' && item.nameAr ? item.nameAr : item.name}
              </h3>
            </div>

            {/* Description */}
            <p className="text-gray-600 text-sm leading-relaxed mb-4 line-clamp-2">
              {language === 'ar' && item.descriptionAr ? item.descriptionAr : item.description}
            </p>

            {/* Nutritional highlights */}
            <div className="flex items-center gap-4 mb-4 text-xs">
              {item.calories && (
                <div className="flex items-center gap-1.5">
                  <div className="w-8 h-8 rounded-full bg-primary/10 flex items-center justify-center">
                    <svg className="w-4 h-4 text-primary" viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2M7,9.5C7,8.67 7.67,8 8.5,8C9.33,8 10,8.67 10,9.5C10,10.33 9.33,11 8.5,11C7.67,11 7,10.33 7,9.5M12,17.23C10.25,17.23 8.71,16.5 7.81,15.42L9.23,14C9.68,14.72 10.75,15.23 12,15.23C13.25,15.23 14.32,14.72 14.77,14L16.19,15.42C15.29,16.5 13.75,17.23 12,17.23M15.5,11C14.67,11 14,10.33 14,9.5C14,8.67 14.67,8 15.5,8C16.33,8 17,8.67 17,9.5C17,10.33 16.33,11 15.5,11Z"/>
                    </svg>
                  </div>
                  <span className="text-primary font-medium">{item.calories} cal</span>
                </div>
              )}

              {item.walkMinutes && (
                <div className="flex items-center gap-1.5">
                  <div className="w-8 h-8 rounded-full bg-gold/10 flex items-center justify-center">
                    <svg className="w-4 h-4 text-gold" viewBox="0 0 24 24" fill="currentColor">
                      <path d="M14.12,10H19V8.2H15.38L13.38,4.87C13.08,4.37 12.54,4.03 11.92,4.03C11.74,4.03 11.58,4.06 11.42,4.11L6,5.8V11H7.8V7.33L9.91,6.67L6,22H7.8L10.67,13.89L13,17V22H14.8V15.59L12.31,11.05L13.04,8.18M14,3.8C15,3.8 15.8,3 15.8,2C15.8,1 15,0.2 14,0.2C13,0.2 12.2,1 12.2,2C12.2,3 13,3.8 14,3.8Z"/>
                    </svg>
                  </div>
                  <span className="text-gold font-medium">{item.walkMinutes} min</span>
                </div>
              )}

              {item.runMinutes && (
                <div className="flex items-center gap-1.5">
                  <div className="w-8 h-8 rounded-full bg-primary/10 flex items-center justify-center">
                    <svg className="w-4 h-4 text-primary" viewBox="0 0 24 24" fill="currentColor">
                      <path d="M13.5,5.5C14.59,5.5 15.5,4.58 15.5,3.5C15.5,2.38 14.59,1.5 13.5,1.5C12.39,1.5 11.5,2.38 11.5,3.5C11.5,4.58 12.39,5.5 13.5,5.5M9.89,19.38L10.89,15L13,17V23H15V15.5L12.89,13.5L13.5,10.5C14.79,12 16.79,13 19,13V11C17.09,11 15.5,10 14.69,8.58L13.69,7C13.29,6.38 12.69,6 12,6C11.69,6 11.5,6.08 11.19,6.08L6,8.28V13H8V9.58L9.79,8.88L8.19,17L3.29,16L2.89,18L9.89,19.38Z"/>
                    </svg>
                  </div>
                  <span className="text-primary font-medium">{item.runMinutes} min</span>
                </div>
              )}
            </div>

            {/* Dietary and allergen section */}
            <div className="flex items-center justify-between pt-4 border-t border-gold/20">
              <div className="flex items-center gap-2">
                {/* Dietary icons */}
                <div className="flex gap-1">
                  {item.halal && (
                    <div className="w-8 h-8 rounded-full bg-emerald-100 flex items-center justify-center" title={language === 'ar' ? 'حلال' : 'Halal'}>
                      <DietaryIcons type="halal" size="w-4 h-4" className="text-emerald-700" />
                    </div>
                  )}
                  {item.glutenFree && (
                    <div className="w-8 h-8 rounded-full bg-emerald-100 flex items-center justify-center" title={language === 'ar' ? 'خالي من الجلوتين' : 'Gluten Free'}>
                      <DietaryIcons type="glutenFree" size="w-4 h-4" className="text-emerald-700" />
                    </div>
                  )}
                  {item.dairyFree && (
                    <div className="w-8 h-8 rounded-full bg-sky-100 flex items-center justify-center" title={language === 'ar' ? 'خالي من الألبان' : 'Dairy Free'}>
                      <DietaryIcons type="dairyFree" size="w-4 h-4" className="text-sky-700" />
                    </div>
                  )}
                  {item.nutFree && (
                    <div className="w-8 h-8 rounded-full bg-purple-100 flex items-center justify-center" title={language === 'ar' ? 'خالي من المكسرات' : 'Nut Free'}>
                      <DietaryIcons type="nutFree" size="w-4 h-4" className="text-purple-700" />
                    </div>
                  )}
                  {item.vegetarian && (
                    <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center" title={language === 'ar' ? 'نباتي' : 'Vegetarian'}>
                      <DietaryIcons type="vegetarian" size="w-4 h-4" className="text-green-700" />
                    </div>
                  )}
                  {item.vegan && (
                    <div className="w-8 h-8 rounded-full bg-lime-100 flex items-center justify-center" title={language === 'ar' ? 'نباتي صرف' : 'Vegan'}>
                      <DietaryIcons type="vegan" size="w-4 h-4" className="text-lime-700" />
                    </div>
                  )}
                </div>

                {/* Allergen warning */}
                {item.allergens && item.allergens.length > 0 && (
                  <div className="flex items-center">
                    <div className="w-px h-6 bg-gray-300 mx-2"></div>
                    <AllergenIcons allergens={item.allergens} size="w-4 h-4" />
                  </div>
                )}
              </div>

              {/* Info button */}
              <button
                onClick={() => setShowNutritionModal(true)}
                className="flex items-center justify-center w-10 h-10 rounded-full bg-gradient-to-br from-gold to-primary text-white hover:shadow-lg transition-all duration-300 hover:scale-110"
                title={language === 'ar' ? 'معلومات غذائية كاملة' : 'Full nutritional information'}
              >
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </button>
            </div>

            {/* Warning indicators */}
            <div className="flex gap-3 mt-3">
              {item.spicyLevel > 0 && (
                <div className="flex items-center gap-1 bg-red-50 text-red-700 px-3 py-1 rounded-full text-xs font-medium">
                  <span>{'🌶️'.repeat(item.spicyLevel)}</span>
                </div>
              )}
              {item.highSodium && (
                <div className="flex items-center gap-1 bg-orange-50 text-orange-700 px-3 py-1 rounded-full text-xs font-medium">
                  <DietaryIcons type="highSodium" size="w-3 h-3" />
                  <span>{language === 'ar' ? 'صوديوم عالي' : 'High Sodium'}</span>
                </div>
              )}
              {item.containsCaffeine && (
                <div className="flex items-center gap-1 bg-amber-50 text-amber-700 px-3 py-1 rounded-full text-xs font-medium">
                  <DietaryIcons type="containsCaffeine" size="w-3 h-3" />
                  <span>{language === 'ar' ? 'كافيين' : 'Caffeine'}</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Nutrition Modal */}
      <NutritionModal 
        item={item}
        isOpen={showNutritionModal}
        onClose={() => setShowNutritionModal(false)}
        language={language}
      />
    </>
  );
};

export default MenuItemCard;
