import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export const menuAPI = {
  // Get all menu items
  getMenuItems: async (category = null) => {
    const params = category ? { category } : {};
    const response = await api.get('/api/menu-items', { params });
    return response.data;
  },

  // Get single menu item
  getMenuItem: async (id) => {
    const response = await api.get(`/api/menu-items/${id}`);
    return response.data;
  },

  // Create menu item
  createMenuItem: async (item) => {
    const response = await api.post('/api/menu-items', item);
    return response.data;
  },

  // Update menu item
  updateMenuItem: async (id, item) => {
    const response = await api.put(`/api/menu-items/${id}`, item);
    return response.data;
  },

  // Delete menu item
  deleteMenuItem: async (id) => {
    const response = await api.delete(`/api/menu-items/${id}`);
    return response.data;
  },

  // Get categories
  getCategories: async () => {
    const response = await api.get('/api/categories');
    return response.data;
  },

  // Create category
  createCategory: async (category) => {
    const response = await api.post('/api/categories', category);
    return response.data;
  },

  // Update category
  updateCategory: async (id, category) => {
    const response = await api.put(`/api/categories/${id}`, category);
    return response.data;
  },

  // Delete category
  deleteCategory: async (id) => {
    const response = await api.delete(`/api/categories/${id}`);
    return response.data;
  },

  // Upload image
  uploadImage: async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    const response = await api.post('/api/upload-image', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  },

  // Get allergen icons
  getAllergenIcons: async () => {
    const response = await api.get('/api/allergen-icons');
    return response.data;
  },

  // Get stats
  getStats: async () => {
    const response = await api.get('/api/stats');
    return response.data;
  },

  // Get settings
  getSettings: async () => {
    const response = await api.get('/api/settings');
    return response.data;
  },

  // Update settings
  updateSettings: async (settings) => {
    const response = await api.put('/api/settings', settings);
    return response.data;
  },
};

export default api;
